'  end;',
'',
'  /*',
'  ** **************************************************************************',
'  */',
'  procedure write_message',
'   (src  in varchar2,',
'    type in varchar2,',
'    msg  in varchar2',
'   ) is',
'  PRAGMA AUTONOMOUS_TRANSACTION;',
'    ts timestamp := SYSTIMESTAMP;',
'  begin',
'    begin',
'      dbms_output.put_line(substrb(ltrim(ts||'' ''||prefix||'' '')||msg,1,4000));',
'    exception when others then null;',
'    end;',
'',
'    insert into trace',
'      (ts, nr, src, type, msg)',
'    values',
'      (ts, nr, src, type, substrb(msg,1,4000))',
'    ;',
'',
'    commit;',
'',
'    l_caller_stored := false;',
'    l_lineno        := '''';',
'    l_owner         := '''';',
'    l_name          := '''';',
'',
