'CREATE OR REPLACE FORCE VIEW "V_MEDIA" ("ID", "CONTENT_TYPE", "FILE_NAME", "CONTENT", "TYPE", "TITLE", "TIMESTAMP", "IDX", "DEVICE", "CARRIER", "TRACKINGNR", "NAME", "FULLTEXT", "CODELIST", "TAGLIST", "HTML_DETAILS", "MONTH", "DAY", "SET_NAME", "IMG"'
