unistr('      ''\D83D\DDD3'' || '' '' || to_char(M.TIMESTAMP, ''dd.mm.yyyy hh24:mi:ss'') || '' - '' || M.idx || ''<br>'' ||'),
unistr('      ''\D83D\DCF7'' || '' '' || M.device || ''<br>'' ||'),
