unistr('      ''\D83D\DCCE'' || '' <a href="'' || ''http://mbp-mschulze.local/ords/dscan/media/files/''|| REGEXP_REPLACE(M.FILE_NAME, ''.jpg$|.jpeg$'', ''.json'') || ''">'' || REGEXP_REPLACE(M.FILE_NAME, ''.jpg$|.jpeg$'', ''.json'') || ''</a>'' || ''<br>'' ||'),
