'CREATE OR REPLACE FORCE VIEW "V_MEDIA_AUTOTAGGING" ("TYPE", "ID", "FILE_NAME", "MATCHCODE1", "MATCHCODE2", "MATCHCODE3", "TAG_NAME", "TAG_VALUE", "RE_PATTERN1", "RE_PATTERN2", "RE_PATTERN3") AS ',
'  (select MAT.type,',
'        MAT.ID,',
'        MRC.file_name,',
'        MRC.Symbology as matchcode1,',
'        MRC.payload   as matchcode2,',
'        ''''            as matchcode3,',
'        MAT.tag_name,',
'        REGEXP_SUBSTR(MRC.Payload, MAT.re_result_substr) as tag_value,',
'        MAT.re_pattern1,',
'        MAT.re_pattern2,',
'        MAT.re_pattern3',
' from   Media_Autotagging MAT',
' join   V_MEDIA_RECOGNIZEDCODES MRC',
'   on   REGEXP_LIKE (MRC.Symbology, MAT.re_pattern1)',
'  and   REGEXP_LIKE (MRC.Payload,   MAT.re_pattern2)',
' where  MAT.type = ''code''',
' UNION ALL',
' select MAT.type,',
'        MAT.ID,',
'        MRT.file_name,',
'        MRT.Text, -- matchcode1',
'        '''',       -- matchcode2',
'        '''',       -- matchcode3',
'        MAT.tag_name,',
'        nvl(REGEXP_SUBSTR(MRT.Text, MAT.re_result_substr), MAT.re_result_substr) as tag_value,',
'        MAT.re_pattern1,',
'        MAT.re_pattern2,',
'        MAT.re_pattern3',
' from   Media_Autotagging MAT',
' join   V_MEDIA_RECOGNIZEDTEXT MRT',
'   on   REGEXP_LIKE (MRT.Text, MAT.re_pattern1)',
' where  MAT.type = ''text''',
'UNION ALL',
' select ''dict'',',
'        ''NAME_DICTIONARY_''||NAD.ID,',
