'CREATE OR REPLACE FORCE EDITIONABLE VIEW "V_MEDIA_METADATA" ("ID", "FILE_NAME", "IDX", "CONTENT_TYPE", "TIMESTAMP", "TEXT", "CCC", "CONFIDENCE", "X", "Y", "W", "H") AS ',
